div.script:
function countUp(elementId, start, end, duration) {
let startTimestamp = null;
const step = (timestamp) => {
if (!startTimestamp) startTimestamp = timestamp;
const progress = Math.min((timestamp - startTimestamp) / duration, 1);
document.getElementById(elementId).innerText = Math.floor(progress * (end - start) + start);
if (progress < 1) {
window.requestAnimationFrame(step);
}
};
window.requestAnimationFrame(step);
}

document.addEventListener('DOMContentLoaded', () => {
countUp('since1982-value', 0, 1982, 2000);
countUp('ugPrograms-value', 0, 14, 2000);
countUp('pgPrograms-value', 0, 5, 2000);
countUp('phdPrograms-value', 0, 1, 2000);
countUp('students-value', 0, 2600, 2000);
countUp('faculty-value', 0, 100, 2000);
});
