document.addEventListener("DOMContentLoaded", function() {
const tabs = document.querySelectorAll(".nav-link");
const tabContents = document.querySelectorAll(".tab-content");

// Define the activateTab function globally
window.activateTab = function(event, tabId) {
if (event) event.preventDefault(); // Prevent default anchor behavior if event exists
tabs.forEach(tab => tab.classList.remove("active"));
tabContents.forEach(content => content.classList.remove("active"));

const activeTab = document.querySelector(`.nav-link[href="#${tabId}"]`);
const activeContent = document.getElementById(tabId);

if (activeTab) activeTab.classList.add("active");
if (activeContent) activeContent.classList.add("active");

history.pushState(null, null, `#${tabId}`);
}

function loadTabFromURL() {
const hash = window.location.hash.substring(1);
if (hash) {
activateTab(null, hash); // No event to pass, so null is passed
}
}

window.addEventListener("popstate", loadTabFromURL);

loadTabFromURL(); // Load the correct tab based on the URL hash on page load
});

document.addEventListener('DOMContentLoaded', function() {
const navToggler = document.getElementById('navToggler');
const topMenuCollapse = document.getElementById('site-navigation');
const Wrap = document.getElementById('wrap');
const foot = document.getElementById('bottom');

navToggler.addEventListener('click', function() {
// Toggle the 'show' class on the topMenuCollapse element
topMenuCollapse.classList.toggle('show');
Wrap.classList.toggle("lock-scroll");
foot.classList.toggle("lock-scroll");
navToggler.classList.toggle('menu-opened');
});
});

document.addEventListener('DOMContentLoaded', function() {
document.querySelectorAll('.nav-item.dropdown .nav-link.dropdown-toggle').forEach(function(link, index)
{
link.addEventListener('click', function(event) {
event.preventDefault();
var navbarCollapse = document.getElementById('emea_top_menu_collapse');
if (!navbarCollapse.classList.contains('show')) { // If the navbar is not collapsed (in mobile view)
if (index === 1) {
window.open("https://emea.embase.in/admission/", "_blank"); // Open in new tab
} else if (index === 2) {
window.location.href = "/academics";
} else if (index === 7) {
window.location.href = "/Home/IQAC/Composition/172";
}
else if (index === 5) {
window.location.href = "/academics-support ";
}
}
});
});
});

$(document).ready(function() {
// Ensure the DOM is fully loaded before attaching event listeners
document.querySelectorAll('.drop-emea .nav-link.dropdown-toggle').forEach(link => {
link.addEventListener('show.bs.dropdown', function() {
if (navbarCollapse.classList.contains('show')) {
this.closest('.nav-item').style.flex = '0 0 100%';
}
});

link.addEventListener('hide.bs.dropdown', function() {
if (navbarCollapse.classList.contains('show')) {
this.closest('.nav-item').style.flex = '0 0 50%';
}
});
});

// Check if the mobile navbar is collapsed or shown
var navbarCollapse = document.getElementById('site-navigation');
if (navbarCollapse.classList.contains('show')) {
// Adjust flex properties or apply styles as needed for mobile view
document.querySelectorAll('.drop-emea .nav-link.dropdown-toggle').forEach(link => {
link.closest('.nav-item').style.flex = '0 0 100%';
});
}
});

document.addEventListener('DOMContentLoaded', function() {
const navToggler = document.getElementById('navToggler');
const menuOverlay = document.getElementById('menu-overlay');

// Function to check and update the display property of the menu overlay
function updateMenuOverlay() {
if (navToggler.classList.contains('menu-opened')) {
menuOverlay.style.display = 'block';
} else {
menuOverlay.style.display = 'none';
}
}

// MutationObserver to detect class changes on the navToggler element
const observer = new MutationObserver(updateMenuOverlay);

// Observe attribute changes (specifically class changes) on the navToggler element
observer.observe(navToggler, { attributes: true, attributeFilter: ['class'] });

// Initial check
updateMenuOverlay();
});

document.addEventListener('DOMContentLoaded', function() {
var searchButton = document.querySelector('.search-button');
var searchInput = document.querySelector('.search-input');

if (searchButton && searchInput) {
searchButton.addEventListener('click', function() {
if (searchInput.style.display === 'none' || searchInput.style.display === '') {
searchInput.style.display = 'block';
} else {
searchInput.style.display = 'none';
}
});
}
});

document.addEventListener('DOMContentLoaded', function() {
// Function to update the width of the menu item
function updateMenuItemWidth() {
var dropdownMenu = document.querySelector('#drop_down.show');
var menuItem = document.querySelector('.menu-item-type-post_type');

if (dropdownMenu) {
menuItem.style.width = '100%';
} else {
menuItem.style.width = ''; // Reset to default if the dropdown is not shown
}
}

// Call the function initially to set the correct width
updateMenuItemWidth();

// Add event listeners for dropdown show and hide events
var dropdownToggle = document.querySelector('.dropdown-toggle');
dropdownToggle.addEventListener('click', function() {
// Update the width when the dropdown is toggled
updateMenuItemWidth();
});
});

