t.script:
document.addEventListener("DOMContentLoaded", function () {
const links = document.querySelectorAll('.programme-link');
const panes = document.querySelectorAll('.programme-pane');

function activateTab(link) {
// Remove all active classes
links.forEach(l => l.classList.remove('active'));
panes.forEach(p => p.classList.remove('active', 'show'));

// Activate clicked link
link.classList.add('active');

// Activate corresponding pane
const targetId = link.getAttribute('href');
const targetPane = document.querySelector(targetId);
if (targetPane) {
targetPane.classList.add('active', 'show');
}
}

// Bind click to each link
links.forEach(link => {
link.addEventListener('click', function (e) {
e.preventDefault();
activateTab(this);
});
});

// Auto-activate first tab on page load
if (links.length > 0) {
activateTab(links[0]);
}
});
